import { GoogleGenAI } from "@google/genai";
import { LanguageDirection } from '../types';

if (!process.env.API_KEY) {
  throw new Error("API_KEY environment variable is not set");
}

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

export const translateText = async (
  text: string,
  direction: LanguageDirection
): Promise<string> => {
  if (!text.trim()) {
    return "";
  }

  const prompt = `Translate the following text from ${direction.split(' to ')[0]} to ${direction.split(' to ')[1]}. Provide only the translated text, without any additional explanations, introductions, or formatting.\n\nText: "${text}"\n\nTranslation:`;

  try {
    const response = await ai.models.generateContent({
      model: 'gemini-2.5-flash',
      contents: prompt,
      config: {
        temperature: 0.3,
        topP: 0.9,
      }
    });

    return response.text.trim();
  } catch (error) {
    console.error("Error translating text with Gemini API:", error);
    throw new Error("Failed to translate text. Please check your connection or API key.");
  }
};
