import { create } from 'zustand';
import { Task, FilterState } from '../types';

interface TaskStore {
  tasks: Task[];
  filters: FilterState;
  selectedTaskId: string | null;

  // Actions
  setTasks: (tasks: Task[]) => void;
  addTask: (task: Task) => void;
  updateTask: (id: string, updates: Partial<Task>) => void;
  deleteTask: (id: string) => void;
  toggleComplete: (id: string) => void;
  reorderTasks: (reorderedTasks: Task[]) => void;

  // Filters
  setFilter: <K extends keyof FilterState>(key: K, value: FilterState[K]) => void;
  clearFilters: () => void;

  // Selection
  selectTask: (id: string | null) => void;
}

export const useTaskStore = create<TaskStore>((set, get) => ({
  tasks: [],
  filters: {
    search: '',
    categoryId: null,
    priority: null,
    assignedTo: null,
    showCompleted: true,
  },
  selectedTaskId: null,

  setTasks: (tasks) => set({ tasks }),

  addTask: (task) => set((state) => ({
    tasks: [...state.tasks, task],
  })),

  updateTask: (id, updates) => set((state) => ({
    tasks: state.tasks.map((task) =>
      task.id === id ? { ...task, ...updates, updatedAt: new Date().toISOString() } : task
    ),
  })),

  deleteTask: (id) => set((state) => ({
    tasks: state.tasks.filter((task) => task.id !== id),
    selectedTaskId: state.selectedTaskId === id ? null : state.selectedTaskId
  })),

  toggleComplete: (id) => set((state) => ({
    tasks: state.tasks.map((task) =>
      task.id === id ? { ...task, completed: !task.completed } : task
    ),
  })),

  reorderTasks: (reorderedTasks) => set((state) => {
    const taskMap = new Map(reorderedTasks.map(t => [t.id, t]));
    const updatedTasks = state.tasks.map(t => taskMap.has(t.id) ? taskMap.get(t.id)! : t);
    return { tasks: updatedTasks };
  }),

  setFilter: (key, value) => set((state) => ({
    filters: { ...state.filters, [key]: value },
  })),

  clearFilters: () => set({
    filters: {
      search: '',
      categoryId: null,
      priority: null,
      assignedTo: null,
      showCompleted: true,
    },
  }),

  selectTask: (id) => set({ selectedTaskId: id }),
}));

import { create } from 'zustand';

interface UIStore {
  sidebarOpen: boolean;
  detailsPanelOpen: boolean;
  quickAddOpen: boolean;
  assignModalOpen: boolean;
  datePickerOpen: boolean;
  selectedTaskForAssign: string | null;

  toggleSidebar: () => void;
  setDetailsPanelOpen: (open: boolean) => void;
  setQuickAddOpen: (open: boolean) => void;
  openAssignModal: (taskId: string) => void;
  closeAssignModal: () => void;
  setDatePickerOpen: (open: boolean) => void;
}

export const useUIStore = create<UIStore>((set) => ({
  sidebarOpen: true,
  detailsPanelOpen: false,
  quickAddOpen: false,
  assignModalOpen: false,
  datePickerOpen: false,
  selectedTaskForAssign: null,

  toggleSidebar: () => set((state) => ({ sidebarOpen: !state.sidebarOpen })),

  setDetailsPanelOpen: (open) => set({ detailsPanelOpen: open }),

  setQuickAddOpen: (open) => set({ quickAddOpen: open }),

  openAssignModal: (taskId) => set({
    assignModalOpen: true,
    selectedTaskForAssign: taskId,
  }),

  closeAssignModal: () => set({
    assignModalOpen: false,
    selectedTaskForAssign: null,
  }),

  setDatePickerOpen: (open) => set({ datePickerOpen: open }),
}));
