import React, { useState, useEffect, useCallback, createContext, useContext, ReactNode } from 'react';
import { User, ViewType, Category, Expense, AnalyticsSummary } from './types';
import { api } from './services/api';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, Legend } from 'recharts';
import { TrendingUp, LayoutDashboard, Wallet, Shapes, Settings, LogOut, Loader2, PlusCircle, MoreVertical, Trash2, Edit, X } from 'lucide-react';

// --- Authentication Context ---
interface AuthContextType {
  user: User | null;
  token: string | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  login: (email: string, password: string) => Promise<void>;
  register: (username: string, email: string, password: string) => Promise<void>;
  logout: () => void;
}

const AuthContext = createContext<AuthContextType | null>(null);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// FIX: Made children prop optional to resolve "Property 'children' is missing" error.
const AuthProvider = ({ children }: { children?: ReactNode }) => {
  const [user, setUser] = useState<User | null>(null);
  const [token, setToken] = useState<string | null>(localStorage.getItem('authToken'));
  const [isLoading, setIsLoading] = useState(true);

  const fetchUser = useCallback(async () => {
    if (token) {
      try {
        const userData = await api.get<User>('/auth/me');
        setUser(userData);
      } catch (error) {
        console.error('Failed to fetch user', error);
        setToken(null);
        localStorage.removeItem('authToken');
      }
    }
    setIsLoading(false);
  }, [token]);

  useEffect(() => {
    fetchUser();
  }, [fetchUser]);

  const handleAuthResponse = (response: { user: User; access_token: string }) => {
    setUser(response.user);
    setToken(response.access_token);
    localStorage.setItem('authToken', response.access_token);
  };

  const login = async (email: string, password: string) => {
    const response = await api.post<{ user: User; access_token: string }>('/auth/login', { email, password });
    handleAuthResponse(response);
  };

  const register = async (username: string, email: string, password: string) => {
    const response = await api.post<{ user: User; access_token: string }>('/auth/register', { username, email, password });
    handleAuthResponse(response);
  };

  const logout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('authToken');
  };

  return (
    <AuthContext.Provider value={{ user, token, isAuthenticated: !!token, isLoading, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// --- Helper UI Components ---
const Input = (props: React.InputHTMLAttributes<HTMLInputElement>) => (
    <input {...props} className="w-full bg-input text-foreground px-3 py-2 rounded-md border border-border focus:outline-none focus:ring-2 focus:ring-ring" />
);

const Button = ({ children, onClick, className = '', variant = 'primary', isLoading = false, ...props }: { children: ReactNode, onClick?: () => void, className?: string, variant?: 'primary' | 'secondary' | 'destructive', isLoading?: boolean } & React.ButtonHTMLAttributes<HTMLButtonElement>) => {
    const baseClasses = 'inline-flex items-center justify-center rounded-md text-sm font-medium transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 disabled:opacity-50 disabled:pointer-events-none px-4 py-2';
    const variantClasses = {
        primary: 'bg-primary text-primary-foreground hover:bg-primary/90',
        secondary: 'bg-secondary text-secondary-foreground hover:bg-secondary/80',
        destructive: 'bg-destructive text-destructive-foreground hover:bg-destructive/90',
    };
    return (
        <button onClick={onClick} className={`${baseClasses} ${variantClasses[variant]} ${className}`} disabled={isLoading} {...props}>
            {isLoading ? <Loader2 className="mr-2 h-4 w-4 animate-spin" /> : null}
            {children}
        </button>
    );
};

// FIX: Made children prop optional to resolve "Property 'children' is missing" error.
const Card = ({ children, className = '' }: { children?: ReactNode, className?: string }) => (
    <div className={`bg-card text-card-foreground border border-border rounded-lg shadow-sm ${className}`}>
        {children}
    </div>
);

// FIX: Made children prop optional to resolve "Property 'children' is missing" error.
const Modal = ({ isOpen, onClose, title, children }: { isOpen: boolean; onClose: () => void; title: string; children?: ReactNode }) => {
    if (!isOpen) return null;
    return (
        <div className="fixed inset-0 bg-background/80 backdrop-blur-sm z-50 flex items-center justify-center">
            <div className="bg-card border border-border rounded-lg shadow-xl w-full max-w-md m-4">
                <div className="flex items-center justify-between p-4 border-b border-border">
                    <h3 className="text-lg font-semibold">{title}</h3>
                    <button onClick={onClose} className="text-muted-foreground hover:text-foreground">
                        <X className="h-5 w-5" />
                    </button>
                </div>
                <div className="p-4">{children}</div>
            </div>
        </div>
    );
};

// --- App Pages/Views ---

const AuthPage = () => {
    const [isLogin, setIsLogin] = useState(true);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [username, setUsername] = useState('');
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const { login, register } = useAuth();

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);
        try {
            if (isLogin) {
                await login(email, password);
            } else {
                await register(username, email, password);
            }
        } catch (err: any) {
            setError(err.message || 'An error occurred.');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-background">
            <Card className="w-full max-w-sm p-8">
                <div className="flex items-center justify-center mb-6">
                    <TrendingUp className="h-8 w-8 text-primary" />
                    <h1 className="text-3xl font-bold ml-2">ExpenseFlow</h1>
                </div>
                <div className="flex justify-center mb-6">
                    <button onClick={() => setIsLogin(true)} className={`px-4 py-2 text-sm font-medium ${isLogin ? 'text-primary border-b-2 border-primary' : 'text-muted-foreground'}`}>Login</button>
                    <button onClick={() => setIsLogin(false)} className={`px-4 py-2 text-sm font-medium ${!isLogin ? 'text-primary border-b-2 border-primary' : 'text-muted-foreground'}`}>Register</button>
                </div>
                <form onSubmit={handleSubmit} className="space-y-4">
                    {!isLogin && (
                        <Input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} required />
                    )}
                    <Input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    <Input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    {error && <p className="text-red-500 text-sm">{error}</p>}
                    <Button type="submit" className="w-full" isLoading={isLoading}>{isLogin ? 'Login' : 'Create Account'}</Button>
                </form>
            </Card>
        </div>
    );
};

const Dashboard = () => {
    const [summary, setSummary] = useState<AnalyticsSummary | null>(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchSummary = async () => {
            try {
                const data = await api.get<AnalyticsSummary>('/analytics/summary');
                setSummary(data);
            } catch (error) {
                console.error("Failed to fetch summary:", error);
            } finally {
                setIsLoading(false);
            }
        };
        fetchSummary();
    }, []);

    if (isLoading) return <div className="flex justify-center items-center h-full"><Loader2 className="h-8 w-8 animate-spin" /></div>;
    if (!summary) return <div>No data available.</div>;

    const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8', '#82ca9d'];

    return (
        <div className="p-6 space-y-6">
            <h2 className="text-3xl font-bold">Dashboard</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <Card className="p-4">
                    <h3 className="text-muted-foreground text-sm">Total Expenses</h3>
                    <p className="text-2xl font-bold">{summary.total_expenses}</p>
                </Card>
                <Card className="p-4">
                    <h3 className="text-muted-foreground text-sm">Total Amount</h3>
                    <p className="text-2xl font-bold">${summary.total_amount.toFixed(2)}</p>
                </Card>
                 <Card className="p-4">
                    <h3 className="text-muted-foreground text-sm">Avg Expense Amount</h3>
                    <p className="text-2xl font-bold">${(summary.total_amount / (summary.total_expenses || 1)).toFixed(2)}</p>
                </Card>
            </div>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <Card className="p-4">
                    <h3 className="font-semibold mb-4">Expenses by Category</h3>
                    <ResponsiveContainer width="100%" height={300}>
                        <PieChart>
                            <Pie data={summary.by_category} dataKey="total" nameKey="category_name" cx="50%" cy="50%" outerRadius={80} fill="#8884d8" label>
                                {summary.by_category.map((entry, index) => (
                                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                ))}
                            </Pie>
                            <Tooltip formatter={(value: number) => `$${value.toFixed(2)}`} />
                            <Legend />
                        </PieChart>
                    </ResponsiveContainer>
                </Card>
                <Card className="p-4">
                    <h3 className="font-semibold mb-4">Monthly Expenses</h3>
                    <ResponsiveContainer width="100%" height={300}>
                        <BarChart data={summary.by_month}>
                            <XAxis dataKey="month" stroke="hsl(215 20.2% 65.1%)" />
                            <YAxis stroke="hsl(215 20.2% 65.1%)" />
                            <Tooltip cursor={{ fill: 'hsl(217.2 32.6% 17.5%)' }} contentStyle={{ backgroundColor: 'hsl(222.2 84% 4.9%)', border: '1px solid hsl(217.2 32.6% 17.5%)' }} />
                            <Bar dataKey="total" fill="hsl(217.2 91.2% 59.8%)" />
                        </BarChart>
                    </ResponsiveContainer>
                </Card>
            </div>
        </div>
    );
};

const Expenses = () => {
    const [expenses, setExpenses] = useState<Expense[]>([]);
    const [categories, setCategories] = useState<Category[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingExpense, setEditingExpense] = useState<Expense | null>(null);

    const fetchExpensesAndCategories = useCallback(async () => {
        setIsLoading(true);
        try {
            const [expenseRes, categoryRes] = await Promise.all([
                api.get<{ expenses: Expense[] }>('/expenses'),
                api.get<{ categories: Category[] }>('/categories')
            ]);
            setExpenses(expenseRes.expenses);
            setCategories(categoryRes.categories);
        } catch (error) {
            console.error("Failed to fetch data:", error);
        } finally {
            setIsLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchExpensesAndCategories();
    }, [fetchExpensesAndCategories]);

    const handleSaveExpense = async (expenseData: Partial<Expense>) => {
        try {
            if (editingExpense) {
                await api.put(`/expenses/${editingExpense.id}`, expenseData);
            } else {
                await api.post('/expenses', expenseData);
            }
            setIsModalOpen(false);
            setEditingExpense(null);
            fetchExpensesAndCategories();
        } catch (error) {
            console.error("Failed to save expense:", error);
        }
    };

    const handleDeleteExpense = async (id: string) => {
        if(window.confirm("Are you sure you want to delete this expense?")) {
            try {
                await api.delete(`/expenses/${id}`);
                fetchExpensesAndCategories();
            } catch (error) {
                console.error("Failed to delete expense:", error);
            }
        }
    };

    // FIX: Add explicit type annotation to fix type inference issue where category was `unknown`.
    const categoryMap: Map<string, Category> = new Map(categories.map(c => [c.id, c]));

    return (
        <div className="p-6">
            <div className="flex justify-between items-center mb-6">
                <h2 className="text-3xl font-bold">Expenses</h2>
                <Button onClick={() => { setEditingExpense(null); setIsModalOpen(true); }} className="flex items-center gap-2">
                    <PlusCircle className="h-5 w-5" />
                    Add Expense
                </Button>
            </div>
            {isLoading ? (
                <div className="flex justify-center items-center h-full"><Loader2 className="h-8 w-8 animate-spin" /></div>
            ) : (
                <Card>
                    <div className="overflow-x-auto">
                        <table className="w-full text-left">
                            <thead className="border-b border-border">
                                <tr>
                                    <th className="p-4">Date</th>
                                    <th className="p-4">Description</th>
                                    <th className="p-4">Category</th>
                                    <th className="p-4 text-right">Amount</th>
                                    <th className="p-4"></th>
                                </tr>
                            </thead>
                            <tbody>
                                {expenses.map(expense => {
                                    const category = categoryMap.get(expense.category_id);
                                    return (
                                    <tr key={expense.id} className="border-b border-border last:border-b-0">
                                        <td className="p-4">{new Date(expense.date).toLocaleDateString()}</td>
                                        <td className="p-4">{expense.description}</td>
                                        <td className="p-4">
                                            {category ? (
                                                <span className="flex items-center gap-2">
                                                    <span style={{color: category.color}}>{category.icon}</span>
                                                    {category.name}
                                                </span>
                                            ) : 'N/A'}
                                        </td>
                                        <td className="p-4 text-right font-mono">${expense.amount.toFixed(2)}</td>
                                        <td className="p-4 text-right">
                                           <Button variant="secondary" size="sm" onClick={() => { setEditingExpense(expense); setIsModalOpen(true); }} className="mr-2"><Edit className="h-4 w-4" /></Button>
                                           <Button variant="destructive" size="sm" onClick={() => handleDeleteExpense(expense.id)}><Trash2 className="h-4 w-4" /></Button>
                                        </td>
                                    </tr>
                                )})}
                            </tbody>
                        </table>
                    </div>
                </Card>
            )}
            <ExpenseForm 
                isOpen={isModalOpen}
                onClose={() => { setIsModalOpen(false); setEditingExpense(null); }}
                onSave={handleSaveExpense}
                expense={editingExpense}
                categories={categories}
            />
        </div>
    );
};


interface ExpenseFormProps {
    isOpen: boolean;
    onClose: () => void;
    onSave: (expense: Partial<Expense>) => void;
    expense: Expense | null;
    categories: Category[];
}

const ExpenseForm = ({ isOpen, onClose, onSave, expense, categories }: ExpenseFormProps) => {
    const [amount, setAmount] = useState('');
    const [description, setDescription] = useState('');
    const [categoryId, setCategoryId] = useState('');
    const [date, setDate] = useState('');

    useEffect(() => {
        if (expense) {
            setAmount(expense.amount.toString());
            setDescription(expense.description);
            setCategoryId(expense.category_id);
            setDate(expense.date.split('T')[0]);
        } else {
            setAmount('');
            setDescription('');
            setCategoryId('');
            setDate(new Date().toISOString().split('T')[0]);
        }
    }, [expense, isOpen]);

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        onSave({ amount: parseFloat(amount), description, category_id: categoryId, date });
    };

    return (
        <Modal isOpen={isOpen} onClose={onClose} title={expense ? 'Edit Expense' : 'Add Expense'}>
            <form onSubmit={handleSubmit} className="space-y-4">
                <Input type="number" placeholder="Amount" value={amount} onChange={(e) => setAmount(e.target.value)} required step="0.01"/>
                <Input type="text" placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} required />
                <select value={categoryId} onChange={(e) => setCategoryId(e.target.value)} required className="w-full bg-input text-foreground px-3 py-2 rounded-md border border-border focus:outline-none focus:ring-2 focus:ring-ring">
                    <option value="" disabled>Select a category</option>
                    {categories.map(c => <option key={c.id} value={c.id}>{c.icon} {c.name}</option>)}
                </select>
                <Input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
                <div className="flex justify-end gap-2">
                    <Button type="button" variant="secondary" onClick={onClose}>Cancel</Button>
                    <Button type="submit">Save</Button>
                </div>
            </form>
        </Modal>
    );
};

const Categories = () => {
    // Placeholder for Categories view
