};

// --- Main Layout ---
const SIDEBAR_ITEMS = [
    { name: 'Dashboard', icon: LayoutDashboard, view: 'dashboard' as ViewType },
    { name: 'Expenses', icon: Wallet, view: 'expenses' as ViewType },
    { name: 'Categories', icon: Shapes, view: 'categories' as ViewType },
    { name: 'Budgets', icon: TrendingUp, view: 'budgets' as ViewType },
];

const Sidebar = ({ activeView, setActiveView }: { activeView: ViewType, setActiveView: (view: ViewType) => void }) => (
    <aside className="w-64 bg-card border-r border-border flex flex-col">
        <div className="p-4 border-b border-border flex items-center">
            <TrendingUp className="h-8 w-8 text-primary" />
            <h1 className="text-xl font-bold ml-2">ExpenseFlow</h1>
        </div>
        <nav className="flex-1 p-4 space-y-2">
            {SIDEBAR_ITEMS.map(item => (
                <button
                    key={item.name}
                    onClick={() => setActiveView(item.view)}
                    className={`w-full flex items-center px-3 py-2 rounded-md text-sm font-medium transition-colors ${activeView === item.view ? 'bg-secondary text-foreground' : 'text-muted-foreground hover:bg-secondary/50 hover:text-foreground'}`}
                >
                    <item.icon className="h-5 w-5 mr-3" />
                    {item.name}
                </button>
            ))}
        </nav>
    </aside>
);

const Header = () => {
    const { user, logout } = useAuth();
    return (
        <header className="h-16 bg-card border-b border-border flex items-center justify-end px-6">
            <div className="flex items-center gap-4">
                <span className="text-sm">Welcome, {user?.username}</span>
                <Button variant="secondary" onClick={logout}>
                    <LogOut className="h-4 w-4 mr-2" />
                    Logout
                </Button>
            </div>
        </header>
    );
}

const MainLayout = () => {
    const [activeView, setActiveView] = useState<ViewType>('dashboard');

    const renderView = () => {
        switch (activeView) {
            case 'dashboard': return <Dashboard />;
            case 'expenses': return <Expenses />;
            case 'categories': return <Categories />;
            case 'budgets': return <Budgets />;
            default: return <Dashboard />;
        }
    };

    return (
        <div className="flex h-screen bg-background text-foreground">
            <Sidebar activeView={activeView} setActiveView={setActiveView} />
            <div className="flex-1 flex flex-col overflow-hidden">
                <Header />
                <main className="flex-1 overflow-y-auto">
                    {renderView()}
                </main>
            </div>
        </div>
    );
};


export default function App() {
    return (
        <AuthProvider>
            <AppContent />
        </AuthProvider>
    );
}

function AppContent() {
    const { isAuthenticated, isLoading } = useAuth();
    if (isLoading) {
        return <div className="min-h-screen flex items-center justify-center"><Loader2 className="h-10 w-10 animate-spin text-primary" /></div>;
    }
    return isAuthenticated ? <MainLayout /> : <AuthPage />;
}

