    user_id: string;
    start_date: string;
    end_date: string | null;
}

export interface AnalyticsSummary {
  total_expenses: number;
  total_amount: number;
  by_category: {
    category_id: string;
    category_name: string;
    total: number;
    percentage: number;
  }[];
  by_month: {
    month: string;
    total: number;
    expense_count: number;
  }[];
}

