import { API_BASE_URL } from '../constants';

const getAuthToken = () => localStorage.getItem('authToken');

export const api = {
  // FIX: Removed trailing comma from generic type parameter list.
  async get<T>(path: string): Promise<T> {
    return this.request<T>(path, 'GET');
  },
  // FIX: Removed trailing comma from generic type parameter list.
  async post<T>(path: string, body: any): Promise<T> {
    return this.request<T>(path, 'POST', body);
  },
  // FIX: Removed trailing comma from generic type parameter list.
  async put<T>(path: string, body: any): Promise<T> {
    return this.request<T>(path, 'PUT', body);
  },
  // FIX: Removed trailing comma from generic type parameter list.
  async delete<T>(path: string, body?: any): Promise<T> {
    return this.request<T>(path, 'DELETE', body);
  },
  // FIX: Removed trailing comma from generic type parameter list.
  async request<T>(path: string, method: string, body?: any): Promise<T> {
    const token = getAuthToken();
    const headers: HeadersInit = {
      'Content-Type': 'application/json',
    };
    if (token) {
      headers['Authorization'] = `Bearer ${token}`;
    }

    const config: RequestInit = {
      method,
      headers,
    };
    if (body) {
      config.body = JSON.stringify(body);
    }

    const response = await fetch(`${API_BASE_URL}${path}`, config);

    if (response.status === 204) {
      return null as T;
    }

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ detail: 'An unknown error occurred' }));
      throw new Error(errorData.detail || `HTTP error! status: ${response.status}`);
    }

    return response.json();
  },
};
