import { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Menu, X, Coffee } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import clsx from 'clsx';

const NAV_LINKS = [
  { name: 'Home', path: '/' },
  { name: 'Menu', path: '/menu' },
  { name: 'About', path: '/about' },
  { name: 'Gallery', path: '/gallery' },
  { name: 'Contact', path: '/contact' },
];

export function Navbar() {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <nav className={clsx(
      "fixed top-0 w-full z-50 transition-all duration-300",
      isScrolled || mobileMenuOpen ? "bg-white/95 backdrop-blur-sm shadow-md" : "bg-transparent"
    )}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-20">
          <Link to="/" className="flex items-center gap-2">
            <Coffee className={clsx("h-8 w-8 transition-colors", isScrolled ? "text-brand-gold" : "text-white md:text-brand-gold")} />
            <span className={clsx("font-serif text-2xl transition-colors", isScrolled ? "text-brand-coffee" : "text-white md:text-brand-coffee")}>
              Greenleaf Coffee
            </span>
          </Link>

          <div className="hidden md:flex gap-8">
            {NAV_LINKS.map((link) => (
              <Link
                key={link.path}
                to={link.path}
                className={clsx(
                  "text-lg transition-colors relative after:content-[''] after:absolute after:left-0 after:bottom-[-4px] after:w-full after:h-[2px] after:bg-brand-gold after:scale-x-0 after:origin-left after:transition-transform hover:after:scale-x-100",
                  location.pathname === link.path
                    ? isScrolled ? 'text-brand-gold font-medium after:scale-x-100' : 'text-white md:text-brand-gold font-medium after:scale-x-100'
                    : isScrolled ? 'text-brand-coffee hover:text-brand-gold' : 'text-white/80 hover:text-white md:text-brand-coffee md:hover:text-brand-gold'
                )}
              >
                {link.name}
              </Link>
            ))}
          </div>

          <button
            className="md:hidden z-10"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
          >
            {mobileMenuOpen ? (
              <X className="h-6 w-6 text-brand-coffee" />
            ) : (
              <Menu className={clsx("h-6 w-6 transition-colors", isScrolled ? "text-brand-coffee" : "text-white")} />
            )}
          </button>
        </div>
      </div>

      <AnimatePresence>
        {mobileMenuOpen && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            className="md:hidden bg-white border-t border-elegant-medium"
          >
            <div className="px-4 py-4 space-y-4">
              {NAV_LINKS.map((link) => (
                <Link
                  key={link.path}
                  to={link.path}
                  onClick={() => setMobileMenuOpen(false)}
                  className={`block text-lg ${
                    location.pathname === link.path
                      ? 'text-brand-gold font-medium'
                      : 'text-brand-coffee'
                  }`}
                >
                  {link.name}
                </Link>
              ))}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </nav>
  );
}

