import React, { useState } from 'react';
import Lightbox from 'yet-another-react-lightbox';
import 'yet-another-react-lightbox/styles.css';
import { motion, AnimatePresence } from 'framer-motion';
import { GALLERY_IMAGES } from '../../data/gallery';

const FILTERS = ['all', 'coffee', 'pastries', 'atmosphere'] as const;
type FilterType = typeof FILTERS[number];

export function GalleryGrid() {
  const [filter, setFilter] = useState<FilterType>('all');
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [lightboxIndex, setLightboxIndex] = useState(0);

  const filteredImages =
    filter === 'all'
      ? GALLERY_IMAGES
      : GALLERY_IMAGES.filter((img) => img.category === filter);

  const lightboxSlides = filteredImages.map((img) => ({ src: img.src }));

  const handleImageClick = (index: number) => {
    setLightboxIndex(index);
    setLightboxOpen(true);
  }

  return (
    <div>
      <div className="flex flex-wrap gap-4 justify-center mb-12">
        {FILTERS.map((category) => (
          <button
            key={category}
            onClick={() => setFilter(category)}
            className={`px-6 py-2 rounded-full transition-colors font-semibold ${
              filter === category
                ? 'bg-brand-gold text-white shadow-md'
                : 'bg-elegant-medium text-brand-coffee hover:bg-brand-gold/20'
            }`}
          >
            {category.charAt(0).toUpperCase() + category.slice(1)}
          </button>
        ))}
      </div>

      <motion.div layout className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        <AnimatePresence>
          {filteredImages.map((image, index) => (
            <motion.div
              key={image.src}
              layout
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.8 }}
              transition={{ duration: 0.3 }}
              className="aspect-square overflow-hidden rounded-lg cursor-pointer shadow-elegant"
              whileHover={{ scale: 1.05 }}
              onClick={() => handleImageClick(index)}
            >
              <img
                src={image.src}
                alt={image.alt}
                className="w-full h-full object-cover"
              />
            </motion.div>
          ))}
        </AnimatePresence>
      </motion.div>

      <Lightbox
        open={lightboxOpen}
        close={() => setLightboxOpen(false)}
        slides={lightboxSlides}
        index={lightboxIndex}
        styles={{ container: { backgroundColor: "rgba(30, 20, 10, .9)" } }}
      />
    </div>
  );
}

