  phone: z.string().optional(),
  subject: z.string().min(5, 'Subject must be at least 5 characters'),
  message: z.string().min(10, 'Message must be at least 10 characters'),
});

type ContactFormData = z.infer<typeof contactSchema>;

export function ContactForm() {
  const [submitted, setSubmitted] = useState(false);

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
    reset,
  } = useForm<ContactFormData>({
    resolver: zodResolver(contactSchema),
  });

  const onSubmit = async (data: ContactFormData) => {
    console.log('Form submitted:', data);
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 1000));
    setSubmitted(true);
    reset();
  };

  return (
    <div className="bg-white p-8 sm:p-10 rounded-lg shadow-elegant-lg">
      <h3 className="font-serif text-3xl text-brand-coffee mb-6">
        Get In Touch
      </h3>
      <AnimatePresence mode="wait">
        {submitted ? (
          <motion.div
            key="success"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="py-12 text-center"
          >
            <div className="w-16 h-16 bg-brand-sage/20 rounded-full flex items-center justify-center mx-auto mb-4">
              <CheckCircle className="h-8 w-8 text-brand-sage" />
            </div>
            <h4 className="text-2xl font-serif text-brand-coffee mb-2">
              Message Sent!
            </h4>
            <p className="text-elegant-dark">
              Thank you for reaching out. We'll get back to you soon.
            </p>
          </motion.div>
        ) : (
          <motion.form
            key="form"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onSubmit={handleSubmit(onSubmit)}
            className="space-y-6"
          >
            <Input {...register('name')} placeholder="Name *" error={errors.name?.message} />
            <Input {...register('email')} type="email" placeholder="Email *" error={errors.email?.message} />
            <Input {...register('phone')} type="tel" placeholder="Phone (Optional)" />
            <Input {...register('subject')} placeholder="Subject *" error={errors.subject?.message} />
            <div>
              <textarea
                {...register('message')}
                rows={5}
                placeholder="Message *"
                className={`w-full px-4 py-3 border border-elegant-medium rounded-lg focus:outline-none focus:ring-2 focus:ring-brand-gold transition-colors ${errors.message ? 'border-red-500 focus:ring-red-500' : ''}`}
              />
              {errors.message && (
                <p className="text-sm text-red-600 mt-1">{errors.message.message}</p>
              )}
            </div>
            <Button type="submit" variant="primary" size="lg" className="w-full" disabled={isSubmitting}>
              {isSubmitting ? (
                <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
              ) : (
                <Send className="mr-2 h-5 w-5" />
              )}
              {isSubmitting ? 'Sending...' : 'Send Message'}
            </Button>
          </motion.form>
        )}
      </AnimatePresence>
    </div>
  );
}

